// a simple class to encapsulate a message

var MSG_STATE_UNSENT = "unsent";
var MSG_STATE_SENT = "sent";
var MSG_STATE_RECEIVED = "received"

var Message = function(msg) {
    this.footer = "...\n";
    this.state = MSG_STATE_UNSENT;
    this.payload = msg;

    this.toMessage = function () {
        return this.payload + '\n' + this.footer;
    }
};

module.exports = {
    Message:  Message,
    MSG_STATE_UNSENT: MSG_STATE_UNSENT,
    MSG_STATE_SENT: MSG_STATE_SENT,
    MSG_STATE_RECEIVED: MSG_STATE_RECEIVED
};
