var message = require(__dirname + '/message');

var parse = function(data) {
    // todo: do something a bit more robust here
    // todo: data should eventually be receieved as a string.
    //       we have to set encoding on reader somewhere else.
    if(data == null) return [];
    var str = data.toString();
    var result = str.split("\n...\n");
    return result;
};

module.exports = {
    parse: parse
};
