// a simple queue that just adds items
var events = require('events');
var util = require('util');

var Queue = function() {
    this.queue = [];
    events.EventEmitter.call(this);

    this.add = function(msg) {
        this.queue.push(msg);
        this.emit('add', msg);       
    }
    
}
util.inherits(Queue, events.EventEmitter);
